pragma solidity ^0.6.0;

contract Gravity {
  event NewGravatar(uint id, address owner, string displayName);
  event UpdatedGravatar(uint id, address owner, string displayName);

  struct Gravatar {
    address owner;
    string displayName;
  }

  Gravatar[] public gravatars;

  mapping (uint => address) public gravatarToOwner;
  mapping (address => uint) public ownerToGravatar;

  // the gravatar at position 0 of gravatars[]
  // is fake
  // it's a mythical gravatar
  // that doesn't really exist
  // this function will be invoked when this contract is deployed
  // but then no more
  constructor() public {
      gravatars.push(Gravatar(address(0), "Mythical"));

      uint id = gravatars.length-1;

      emit NewGravatar(id, address(0), "Mythical");
  }

  function createGravatar(string calldata _displayName) external {
    require(ownerToGravatar[msg.sender] == 0, "Address already has a gravatar");
    gravatars.push(Gravatar(msg.sender, _displayName));

    uint id = gravatars.length-1;

    gravatarToOwner[id] = msg.sender;
    ownerToGravatar[msg.sender] = id;

    emit NewGravatar(id, msg.sender, _displayName);
  }

  function updateGravatarName(string calldata _displayName ) external {
    require(ownerToGravatar[msg.sender] != 0, "User doesn't have gravatars");

    uint id = ownerToGravatar[msg.sender];

    gravatars[id].displayName = _displayName;
    emit UpdatedGravatar(id, msg.sender, _displayName);
  }

  function getGravatar(address owner) external view returns (string memory) {
    uint id = ownerToGravatar[owner];
    return (gravatars[id].displayName);
  }

  function getOwner(uint id) external view returns (address) {
    address owner = gravatarToOwner[id];
    return (owner);
  }

  function getId() external view returns (uint) {
    uint id = ownerToGravatar[msg.sender];
    return (id);
  }
}
